import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-book-list',
  templateUrl: './book-list.component.html',
  styles: [
  ]
})
export class BookListComponent implements OnInit {
  nbBooks: number=2;
  books=[  {
    title: 'To Kill a Mockingbird',
    description: 'The unforgettable novel of a childhood in a sleepy Southern town and the crisis of conscience that rocked it.',
    imageURL: 'https://images.penguinrandomhouse.com/cover/9780446310789',
    author: 'Harper Lee',
    publishedDate: new Date('1960-07-11')
  },
  {
    title: 'The Great Gatsby',
    description: 'Jay Gatsby, a self-made millionaire, passionately pursues the elusive Daisy Buchanan. Nick Carraway, a young newcomer to Long Island, is drawn into their world of obsession, greed, and danger.',
    imageURL: 'https://images.penguinrandomhouse.com/cover/9780743273565',
    author: 'F. Scott Fitzgerald',
    publishedDate: new Date('1925-04-10')
  },
  {
    title: 'Pride and Prejudice',
    description: 'In early nineteenth-century England, a spirited young woman copes with the romantic entanglements of her sisters, and her own heart.',
    imageURL: 'https://images.penguinrandomhouse.com/cover/9780679783268',
    author: 'Jane Austen',
    publishedDate: new Date('1813-01-28')
  }
]
  constructor(){

  }
  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }

}
